const marchMove = (
  output: Output,
  march: AloDirection,
  stackFirst: boolean,
  safezone?: string,
): string => {
  const move = {
    'front': output.forward,
    'back': output.backward,
    'left': output.left,
    'right': output.right,
  }[march];
  if (safezone !== undefined)
    return move!({ dest: safezone });
  return move!({ dest: stackFirst ? output.stacks!() : output.spread!() });
};

